import './App.css';
import React, { useState } from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import { Transition } from "react-transition-group";
import List from './components/list/List';
import Profile from './components/profile/Profile'

function App() {

  const [inProp, setInProp] = useState(true);

  const fetchAllPokemons = () => {
    return fetch('http://localhost:8080/allPokemons')
      .then(res =>  res.json())
      .catch(err => `Something went wrong: ${err}`)
  }


  const duration = 900;

  const defaultStyle = {
    transition: `opacity ${duration}ms ease-in-out`,
    opacity: 0,
  }

  const transitionStyles = {
    entering: { opacity: 1 },
    entered:  { opacity: 1 },
    exiting:  { opacity: 0.5 },
    exited:  { opacity: 0.5 },
  };

  return (
    <BrowserRouter>

<Transition in={inProp} exit={inProp} timeout={duration}>
    {state => (
      <div style={{
        ...defaultStyle,
        ...transitionStyles[state]
      }}>

      <Switch>

        <Route exact path="/">
          <div className="App">
            <h1 className="main-title">Pok√©dex</h1>
            <List fetchPokemons={fetchAllPokemons()} />
          </div>
        </Route>

        <Route path="/profile" onClick={() => setInProp(true)}>
          <Profile />
        </Route>

      </Switch>

      </div>
    )}
  </Transition>


    </BrowserRouter>
  );
}

export default App;
