import React, { useEffect } from 'react';
import Chart from 'chart.js';

const Stats = ({ pokemonStats, name }) => {
    const statsLabels = pokemonStats.map(pokemon => pokemon.stat.name);
    const baseStats = pokemonStats.map(pokemon => pokemon.base_stat);
    const chartRef = React.createRef();

    let myChart;

    useEffect(() => {
        myChart = new Chart(chartRef.current, {
            type: 'bar', 
            data: {
                labels: statsLabels,
                datasets: [{
                    data: baseStats, 
                    backgroundColor: [
                        '#ff9aa2',
                        '#c7ceea',
                        '#ffb7b2',
                        '#e2f0cb',
                        '#ffdac1',
                        '#b5ead7'
                    ]
                }]
            },
            options: {
                legend: {
                    display: false
                },
                scales: {
                    yAxes: [{
                        ticks: {
                            suggestedMin: 0,
                            suggestedMax: 200,
                        }
                    }],
                    xAxes: [{ 
                        scaleLabel: {
                          display: true,
                          labelString: "GDP (PPP)"
                        }
                      }]
                },
                title: {
                    display: true,
                    text: `STATS ${name.toUpperCase()}`
                },
                labels: {
                    display: false
                }
            }
        });
    }, [])

    return (
        <canvas ref={chartRef} />
    )
}

export default Stats;
